INSERT INTO `adcog_school` (`id`, `name`, `year`, `wikipedia`, `description`, `graduation`, `graduates`, `created`, `updated`, `uri`, `filename`, `extension`, `mime`, `path`, `size`, `uniqid`, `md5`) VALUES
(1, 'Léonard de Vinci', 2007, 'http://fr.wikipedia.org/wiki/L%C3%A9onard_de_Vinci', '<p>Léonard de Vinci, né à Vinci le 15 avril 1452 et mort à Amboise le 2 mai 1519, est un peintre florentin et un homme d\'esprit universel, à la fois artiste, scientifique, ingénieur, inventeur, anatomiste, peintre, sculpteur, architecte, urbaniste, botaniste, musicien, poète, philosophe et écrivain.</p>', '', NULL, '2013-12-28 23:56:09', '2014-11-10 12:08:43', '/files/prod/school/0/0/0/0/0/0/0/1.jpeg', 'Leonardo_self.jpg', 'jpeg', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/0/1.jpeg', 69043, '54609c948f21a', NULL),
(2, 'Francisco Varela', 2008, 'http://fr.wikipedia.org/wiki/Francisco_Varela', '<p>Francisco Varela est l\'auteur de nombreux ouvrages en biologie théorique et sciences cognitives ayant eu une influence théorique largement au-delà de ses domaines d\'études initiaux.</p>', '', NULL, '2013-12-28 23:56:09', '2014-11-10 12:07:19', '/files/prod/school/0/0/0/0/0/0/0/2.jpeg', 'Francisco_Varela.jpg', 'jpeg', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/0/2.jpeg', 291133, '54609c38476a8', NULL),
(3, 'Allan Turing', 2009, 'http://fr.wikipedia.org/wiki/Alan_Turing', '<p>Alan Mathison Turing (23 juin 1912 - 7 juin 1954), est un mathématicien, cryptologue et informaticien britannique.</p>', '', NULL, '2013-12-28 23:56:09', '2014-11-10 12:10:13', '/files/prod/school/0/0/0/0/0/0/0/3.jpeg', 'Alan_Turing_Aged_16.jpg', 'jpeg', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/0/3.jpeg', 69317, '54609cfef3330', NULL),
(4, 'Edgar Morin', 2010, 'http://fr.wikipedia.org/wiki/Edgar_Morin', '<p>Edgar Nahoum, dit Edgar Morin, né à Paris le 8 juillet 1921, est un sociologue et philosophe français.</p>\r\n\r\n<blockquote>\r\n<p>C’est-à-dire que je parle de la collaboration du monde extérieur et de notre esprit pour construire la réalité</p>\r\n<footer>Edgar Morin</footer>\r\n</blockquote>', '', NULL, '2013-12-28 23:56:09', '2014-11-10 12:45:14', '/files/prod/school/0/0/0/0/0/0/0/4.jpeg', 'edgar_morin.jpg', 'jpeg', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/0/4.jpeg', 25783, '54284c2a3933f', NULL),
(5, 'Jean-Michel Truong', 2011, 'http://fr.wikipedia.org/wiki/Jean-Michel_Truong', '<p>Jean-Michel Truong, né Jean-Michel Charles Xuân Truong Ngoc, est né le 16 avril 1950 à Wasselonne en Alsace. Psychologue et philosophe de formation, il est consultant et écrivain.</p>', '', NULL, '2013-12-28 23:56:09', '2014-11-10 12:11:29', '/files/prod/school/0/0/0/0/0/0/0/5.jpeg', 'Jmtruong6.jpg', 'jpeg', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/0/5.jpeg', 88498, '54609d6127452', NULL),
(6, 'Donald Norman', 2012, 'http://fr.wikipedia.org/wiki/Donald_Norman', '<p>Donald Norman (né le 25 décembre 1935) est un Professeur émérite en Sciences cognitives de l\'Université de Californie.</p>', '', NULL, '2013-12-28 23:56:09', '2014-11-10 12:13:59', '/files/prod/school/0/0/0/0/0/0/0/6.jpeg', 'Donald_Norman_at_AWF05.jpg', 'jpeg', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/0/6.jpeg', 268514, '54609df716563', NULL),
(7, 'Isaac Asimov', 2013, 'http://fr.wikipedia.org/wiki/Isaac_Asimov', '<p>Isaac Asimov, né vers le 2 janvier 1920 à Petrovitchi (en Russie) et mort le 6 avril 1992 à New York aux États-Unis, est un écrivain américano-russe, naturalisé en 1928, surtout connu pour ses oeuvres de science-fiction et ses livres de vulgarisation scientifique.</p>', '', 34, '2014-03-15 20:25:34', '2017-12-08 00:02:12', '/files/prod/school/0/0/0/0/0/0/0/7.jpeg', '640px-Isaac.Asimov01.jpg', 'jpeg', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/0/7.jpeg', 112804, '54609e2a975cd', NULL),
(8, 'Tim Berners-Lee', 2014, 'http://fr.wikipedia.org/wiki/Tim_Berners-Lee', '<p><b>Timothy John Berners-Lee</b><span>,&nbsp;</span><a href=\"http://fr.wikipedia.org/wiki/Ordre_de_l%27Empire_britannique\">KBE</a><span>, né le&nbsp;</span><span class=\"date-lien nowrap datasortkey\" data-sort-value=\"1955-06-08\"><a href=\"http://fr.wikipedia.org/wiki/8_juin\" title=\"8 juin\">8</a>&nbsp;<a href=\"http://fr.wikipedia.org/wiki/Juin_1955\" title=\"Juin 1955\">juin</a>&nbsp;<a href=\"http://fr.wikipedia.org/wiki/1955\" title=\"1955\">1955</a></span><span>&nbsp;à&nbsp;</span><a href=\"http://fr.wikipedia.org/wiki/Londres\" title=\"Londres\">Londres</a><span>, est un citoyen britannique, principal inventeur du&nbsp;</span><a href=\"http://fr.wikipedia.org/wiki/World_Wide_Web\" title=\"World Wide Web\">World Wide Web</a><span>&nbsp;(WWW) au tournant des années 1990. En juillet&nbsp;</span><a href=\"http://fr.wikipedia.org/wiki/2004\" title=\"2004\">2004</a><span>, il est anobli par la reine&nbsp;</span><a href=\"http://fr.wikipedia.org/wiki/%C3%89lisabeth_II_du_Royaume-Uni\" title=\"Élisabeth II du Royaume-Uni\">Élisabeth II</a><span>&nbsp;pour ce travail et son nom officiel devient&nbsp;</span><span class=\"nowrap\">Sir Timothy John Berners-Lee</span><span>. Depuis&nbsp;</span><a href=\"http://fr.wikipedia.org/wiki/1994\" title=\"1994\">1994</a><span>, il préside le&nbsp;</span><a href=\"http://fr.wikipedia.org/wiki/World_Wide_Web_Consortium\" title=\"World Wide Web Consortium\">World Wide Web Consortium</a><span>&nbsp;(W3C), organisme qu\'il a fondé.</span></p>', '', 37, '2014-03-15 20:25:53', '2017-12-08 00:01:41', '/files/prod/school/0/0/0/0/0/0/0/8.jpeg', '640px-Tim_Berners-Lee_closeup.jpg', 'jpeg', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/0/8.jpeg', 80367, '543c11ff84061', NULL),
(9, 'Ada Lovelace', 2015, 'https://fr.wikipedia.org/wiki/Ada_Lovelace', '<p>Ada Lovelace, de son nom complet&nbsp;Augusta Ada King, comtesse de Lovelace, née&nbsp;Ada Byron&nbsp;le&nbsp;<a href=\"https://fr.wikipedia.org/wiki/10_d%C3%A9cembre\" title=\"10 décembre\">10</a>&nbsp;<a href=\"https://fr.wikipedia.org/wiki/D%C3%A9cembre_1815\" title=\"Décembre 1815\">décembre</a>&nbsp;<a href=\"https://fr.wikipedia.org/wiki/1815\" title=\"1815\">1815</a>&nbsp;à&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Londres\" title=\"Londres\">Londres</a>&nbsp;et morte le&nbsp;<a href=\"https://fr.wikipedia.org/wiki/27_novembre\" title=\"27 novembre\">27</a>&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Novembre_1852\" title=\"Novembre 1852\">novembre</a>&nbsp;<a href=\"https://fr.wikipedia.org/wiki/1852\" title=\"1852\">1852</a>&nbsp;à&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Marylebone\" title=\"Marylebone\">Marylebone</a>&nbsp;dans la même ville, fille de&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Lord_Byron\" title=\"Lord Byron\">Lord Byron</a>, est une pionnière de la science informatique. Elle est principalement connue pour avoir réalisé le premier&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Programme_informatique\" title=\"Programme informatique\">programme informatique</a>, lors de son travail sur un ancêtre de l\'<a href=\"https://fr.wikipedia.org/wiki/Ordinateur\" title=\"Ordinateur\">ordinateur</a>&nbsp;: la&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Machine_analytique\" title=\"Machine analytique\">machine analytique</a>de&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Charles_Babbage\" title=\"Charles Babbage\">Charles Babbage</a>.</p>', '', 46, '2014-03-15 20:26:10', '2017-12-08 00:00:35', '/files/prod/school/0/0/0/0/0/0/0/9.png', 'ada_lovelace.jpg', 'png', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/0/9.png', 375942, '565cd17199b25', '64c9449fc84bea3aa64353d3e3307d2b'),
(10, 'Claude Shannon', 2016, 'https://fr.wikipedia.org/wiki/Claude_Shannon', '<p><strong>Claude Elwood Shannon</strong> (<a href=\"https://fr.wikipedia.org/wiki/30_avril\">30</a> <a href=\"https://fr.wikipedia.org/wiki/Avril_1916\">avril</a> <a href=\"https://fr.wikipedia.org/wiki/1916\">1916</a> à <a href=\"https://fr.wikipedia.org/wiki/Petoskey\">Petoskey</a>, <a href=\"https://fr.wikipedia.org/wiki/Michigan\">Michigan</a> - <a href=\"https://fr.wikipedia.org/wiki/24_f%C3%A9vrier\">24</a> <a href=\"https://fr.wikipedia.org/wiki/F%C3%A9vrier_2001\">février</a> <a href=\"https://fr.wikipedia.org/wiki/2001\">2001</a> à <a href=\"https://fr.wikipedia.org/wiki/Medford_(Massachusetts)\">Medford</a>, <a href=\"https://fr.wikipedia.org/wiki/Massachusetts\">Massachusetts</a>) est un <a href=\"https://fr.wikipedia.org/wiki/Ing%C3%A9nieur\">ingénieur</a> en <a href=\"https://fr.wikipedia.org/wiki/G%C3%A9nie_%C3%A9lectrique\">génie électrique</a> et <a href=\"https://fr.wikipedia.org/wiki/Math%C3%A9maticien\">mathématicien</a> <a href=\"https://fr.wikipedia.org/wiki/%C3%89tats-Unis\">américain</a>. Il est l\'un des pères, si ce n\'est le père fondateur, de la <a href=\"https://fr.wikipedia.org/wiki/Th%C3%A9orie_de_l%27information\">théorie de l\'information</a>.</p>', '', 48, '2014-03-15 20:26:22', '2017-01-21 12:07:31', '/files/prod/school/0/0/0/0/0/0/1/0.jpeg', 'shannon.jpg', 'jpeg', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/1/0.jpeg', 15695, '588340f2e1afb', '8692e90b60afd902e7d5e1e826aad6e1'),
(11, 'James Reason', 2017, 'https://fr.wikipedia.org/wiki/James_Reason', '<p><b>James Reason</b>, né le&nbsp;<a href=\"https://fr.wikipedia.org/wiki/1er_mai\" style=\"background-image: none; background-position: initial;\" title=\"1er mai\">1er</a>&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Mai_1938\" style=\"background-image: none; background-position: initial;\" title=\"Mai 1938\">mai</a>&nbsp;<a href=\"https://fr.wikipedia.org/wiki/1938\" style=\"background-image: none; background-position: initial;\" title=\"1938\">1938</a>&nbsp;à&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Watford\" style=\"background-image: none; background-position: initial;\" title=\"Watford\">Watford</a>&nbsp;dans le&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Hertfordshire\" style=\"background-image: none; background-position: initial;\" title=\"Hertfordshire\">Hertfordshire</a>, est un expert en&nbsp;<a class=\"mw-redirect\" href=\"https://fr.wikipedia.org/wiki/Facteurs_humains\" style=\"background-image: none; background-position: initial;\" title=\"Facteurs humains\">facteurs humains</a>.</p>\r\n\r\n<p>Il est membre de la&nbsp;<a href=\"https://fr.wikipedia.org/wiki/British_Psychological_Society\" style=\"background-image: none; background-position: initial;\" title=\"British Psychological Society\">British Psychological Society</a>, de la&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Royal_Aeronautical_Society\" style=\"background-image: none; background-position: initial;\" title=\"Royal Aeronautical Society\">Royal Aeronautical Society</a>, de la&nbsp;<a href=\"https://fr.wikipedia.org/wiki/British_Academy\" style=\"background-image: none; background-position: initial;\" title=\"British Academy\">British Academy</a>&nbsp;et membre honoraire du&nbsp;<a href=\"https://en.wikipedia.org/wiki/Royal_College_of_General_Practitioners\">Royal College of General Practitioners</a>. Il est nommé commandeur de l\'<a href=\"https://fr.wikipedia.org/wiki/Ordre_de_l%27Empire_britannique\" style=\"background-image: none; background-position: initial;\" title=\"Ordre de l\'Empire britannique\">ordre de l\'Empire britannique</a>&nbsp;en 2003 en récompense de ses travaux sur la réduction des risques en&nbsp;<a class=\"mw-redirect\" href=\"https://fr.wikipedia.org/wiki/Milieu_hospitalier\" style=\"background-image: none; background-position: initial;\" title=\"Milieu hospitalier\">milieu hospitalier</a>.</p>', '', 59, '2014-09-10 09:52:08', '2017-11-24 22:47:25', '/files/prod/school/0/0/0/0/0/0/1/1.jpeg', 'jr_photo.jpg', 'jpeg', 'image/jpeg', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/1/1.jpeg', 164749, '5a1892656bc88', 'fb45280596233c0559ae5040ca0a5f86'),
(12, 'Sans promotion', 2003, 'http://fr.wikipedia.org/wiki/%C3%89cole_nationale_sup%C3%A9rieure_de_cognitique', '<p>L’École nationale supérieure de cognitique est une école publique d’ingénieurs située au domaine universitaire de Talence Pessac Gradignan, près de Bordeaux.</p>', '', NULL, '2015-04-01 20:11:07', '2015-04-01 20:17:49', '/files/prod/school/0/0/0/0/0/0/1/2.png', 'École_nationale_supérieure_de_cognitique,_Logo_2014.png', 'png', 'image/png', '/var/www/adcog/web/files/prod/school/0/0/0/0/0/0/1/2.png', 87867, '551c34bb17d14', NULL),
